import React, { useState } from 'react';
import { View, StyleSheet, Text, Image, ImageBackground, ScrollView, TouchableOpacity, FlatList } from 'react-native';
import { Feather } from '@expo/vector-icons';

const Post = ({navigation}) => {


    const image = { uri: 
      "https://images.unsplash.com/photo-1502791451862-7bd8c1df43a7?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"
    };



  return (
       <View style={{backgroundColor: '#fff', flex: 1}}>
         <ImageBackground 
           source={image}
           style={styles.image}
           imageStyle={{borderBottomRightRadius: 30, borderBottomLeftRadius: 30}}
         >

          <Text style={styles.TagLine}>Discover Switzerland</Text>
          <Text style={styles.PlaceName}>Explore the scenic beauty of Switzerland </Text>

          <TouchableOpacity style={{position: 'absolute', left: 20, top: 40, backgroundColor: '#ff6200', padding: 10,borderRadius: 40}}>
            <Feather name='arrow-left' size={24} color='#fff' />
          </TouchableOpacity>

          <TouchableOpacity style={{position: 'absolute', right: 20, top: 40, backgroundColor: '#ff6200', padding: 10,borderRadius: 40}}>
            <Feather name='heart' size={24} color='#fff' />
          </TouchableOpacity>

          <TouchableOpacity style={styles.BookTicketBtn}>
            <Text style={styles.bookTicketText}>Book Now</Text>
          </TouchableOpacity>

         </ImageBackground>
       </View>
  );
}

const styles = StyleSheet.create({
  container: { 
     flex: 1,
    alignItems: 'center', 
    justifyContent: 'center', 
    backgroundColor: '#fff',
  },
  image: {
    height: 380,
    justifyContent: 'flex-end'
  },
 TagLine : {
   color: 'white',
   fontSize: 16,
   fontWeight: 'bold',
   paddingHorizontal: 14,
   marginVertical: 6
 }, 
 PlaceName: {
  color: 'white',
  fontSize: 24,
  fontWeight: 'bold',
  paddingHorizontal: 14,
  marginBottom: 30
 },
 BookTicketBtn: {
   position: 'absolute',
   right: 12,
   top: 350,
   backgroundColor: '#ff6200',
   padding: 16,
   borderRadius: 40
 },
 bookTicketText: {
   color: '#fff',
   fontSize: 14
 }
});
export default Post;
